var numbers = [];
for (var i = 0; i < 10; i++) {
    numbers[i] = Number(prompt('Enter a number: ') || '0');
}
for (var i = 0; i < i; i++) {
    alert(numbers[i]);
}
var count = parseInt(prompt('How many numbers do you want to enter?') || '0', 10);
for (var i = 0; i < count; i++) {
    numbers[i] = parseFloat(prompt('Enter a number: ') || '0');
}
for (var i = 0; i < count; i++) {
    alert(numbers[i]);
}
